import React, { useState } from 'react';

const agendaImg = "https://images.unsplash.com/photo-1515378791036-0648a3ef77b2?auto=format&fit=crop&w=400&q=80";

function App() {
  const [users, setUsers] = useState([]);
  const [registerData, setRegisterData] = useState({ email: '', password: '' });
  const [loginData, setLoginData] = useState({ email: '', password: '' });
  const [loggedInUser, setLoggedInUser] = useState(null);
  const [error, setError] = useState('');
  const [screen, setScreen] = useState('home'); // 'home' | 'register' | 'login' | 'contacts'
  const [contacts, setContacts] = useState([]);
  const [contactData, setContactData] = useState({ name: '', phone: '' });

  // Registro de usuario
  const handleRegister = (e) => {
    e.preventDefault();
    if (users.find(u => u.email === registerData.email)) {
      setError('El email ya está registrado');
      return;
    }
    setUsers([...users, registerData]);
    setRegisterData({ email: '', password: '' });
    setError('');
    alert('Usuario registrado correctamente');
    setScreen('login');
  };

  // Login de usuario
  const handleLogin = (e) => {
    e.preventDefault();
    const user = users.find(
      u => u.email === loginData.email && u.password === loginData.password
    );
    if (user) {
      setLoggedInUser(user.email);
      setLoginData({ email: '', password: '' });
      setError('');
      setScreen('contacts');
    } else {
      setError('Email o contraseña incorrectos');
    }
  };

  // Logout
  const handleLogout = () => {
    setLoggedInUser(null);
    setScreen('home');
    setContacts([]);
  };

  // Agregar contacto
  const handleAddContact = (e) => {
    e.preventDefault();
    if (!contactData.name || !contactData.phone) {
      setError('Completa ambos campos');
      return;
    }
    setContacts([...contacts, contactData]);
    setContactData({ name: '', phone: '' });
    setError('');
  };

  return (
    <div
      style={{
        minHeight: '100vh',
        width: '100vw',
        background: '#FFD1DC',
        color: '#000',
        fontFamily: 'Arial, sans-serif',
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
        justifyContent: 'flex-start',
        paddingTop: 40
      }}
    >
      <img
        src={agendaImg}
        alt="Agenda"
        style={{
          width: 120,
          height: 120,
          objectFit: 'cover',
          borderRadius: '50%',
          marginBottom: 20,
          boxShadow: '0 2px 8px rgba(0,0,0,0.15)'
        }}
      />
      <h1 style={{ textAlign: 'center', fontSize: '2.5rem', marginBottom: 30 }}>Agenda</h1>

      <div style={{ width: 400, background: 'rgba(255,255,255,0.3)', borderRadius: 10, padding: 20, boxShadow: '0 2px 8px rgba(0,0,0,0.08)' }}>
        {screen === 'home' && (
          <div style={{ textAlign: 'center' }}>
            <button onClick={() => { setScreen('login'); setError(''); }}>Iniciar sesión</button>
            <button onClick={() => { setScreen('register'); setError(''); }} style={{ marginLeft: 10 }}>Registro de usuario</button>
          </div>
        )}

        {screen === 'register' && (
          <>
            <h2>Registro de usuario</h2>
            <form onSubmit={handleRegister}>
              <input
                type="email"
                placeholder="Email"
                value={registerData.email}
                onChange={e => setRegisterData({ ...registerData, email: e.target.value })}
                required
                style={{ width: '100%', marginBottom: 10, padding: 8 }}
              />
              <input
                type="password"
                placeholder="Contraseña"
                value={registerData.password}
                onChange={e => setRegisterData({ ...registerData, password: e.target.value })}
                required
                style={{ width: '100%', marginBottom: 10, padding: 8 }}
              />
              <button type="submit" style={{ width: '100%', padding: 8 }}>Registrar</button>
            </form>
            <button onClick={() => { setScreen('home'); setError(''); }} style={{ marginTop: 10, width: '100%', padding: 8 }}>Volver</button>
            {error && <p style={{ color: 'red' }}>{error}</p>}
          </>
        )}

        {screen === 'login' && (
          <>
            <h2>Iniciar sesión</h2>
            <form onSubmit={handleLogin}>
              <input
                type="email"
                placeholder="Email"
                value={loginData.email}
                onChange={e => setLoginData({ ...loginData, email: e.target.value })}
                required
                style={{ width: '100%', marginBottom: 10, padding: 8 }}
              />
              <input
                type="password"
                placeholder="Contraseña"
                value={loginData.password}
                onChange={e => setLoginData({ ...loginData, password: e.target.value })}
                required
                style={{ width: '100%', marginBottom: 10, padding: 8 }}
              />
              <button type="submit" style={{ width: '100%', padding: 8 }}>Ingresar</button>
            </form>
            <button onClick={() => { setScreen('home'); setError(''); }} style={{ marginTop: 10, width: '100%', padding: 8 }}>Volver</button>
            {error && <p style={{ color: 'red' }}>{error}</p>}
          </>
        )}

        {screen === 'contacts' && loggedInUser && (
          <div>
            <h2>Bienvenido, {loggedInUser}!</h2>
            <button onClick={handleLogout} style={{ padding: 8, marginBottom: 20 }}>Cerrar sesión</button>
            <h3>Agregar contacto</h3>
            <form onSubmit={handleAddContact}>
              <input
                type="text"
                placeholder="Nombre"
                value={contactData.name}
                onChange={e => setContactData({ ...contactData, name: e.target.value })}
                required
                style={{ width: '100%', marginBottom: 10, padding: 8 }}
              />
              <input
                type="text"
                placeholder="Teléfono"
                value={contactData.phone}
                onChange={e => setContactData({ ...contactData, phone: e.target.value })}
                required
                style={{ width: '100%', marginBottom: 10, padding: 8 }}
              />
              <button type="submit" style={{ width: '100%', padding: 8 }}>Agregar</button>
            </form>
            {error && <p style={{ color: 'red' }}>{error}</p>}
            <h3 style={{ marginTop: 20 }}>Contactos</h3>
            <ul>
              {contacts.map((contact, idx) => (
                <li key={idx}>
                  <strong>{contact.name}</strong> - {contact.phone}
                </li>
              ))}
            </ul>
          </div>
        )}
      </div>
    </div>
  );
}
export default App;
